        <details>
          <summary>
            thicknessmode
          </summary>
          <div style="margin-left: 22px">
            <dl>
              <dt>Parent:</dt>
              <dd>data[type=mesh3d].colorbar</dd>
              <dt>Type:</dt>
              <dd>enumerated , one of ( "fraction" | "pixels" )</dd>
              <dt>Default:</dt>
              <dd>"pixels"</dd>
